import { useSelector } from "react-redux";

export default function dashPanel() {
    const [user, setUser] = useState({});
    const [comment, setCOmment] = useState({});
    const [posts, setPosts] = useState({});
    const [totalPost, setTotalPost] = useState(0);
    const [totalComment, setTotalComment] = useState(0);
    const [totalUser, setTotalUser] = useState(0);
    const [recentPost, setRecentPosts] = useState({});
    const [recentComment, setRecentComment] = useState({});
    const [recentUser, setRecentUsers] = useState({});
    const { currentUser } = useSelector((state) => state.user);

    useEffect(() => {
        const fetchUsers = async () => {
            const res = await fetch("/api/user/getUser");
            if(res.ok){
                const data = await res.json();
                // setUser(data.user)
            }
        };
        const fetchComments = async () => {
            const res = await fetch("/api/comment/getComment");
            if(res.ok){
                const data = await res.json();
                // setUser(data.user)
            }
        };
        const fetchPosts = async () => {
            const res = await fetch("/api/post/posts");
            if(res.ok){
                const data = await res.json();
                // setUser(data.user)
            }
        };
    }, [currentUser.payload]);

    return <div>Dashboard</div>;
}
